import React, { useState, useEffect } from "react";
import "./TodoList.css";

const TodoList: React.FC = () => {
  const [inputValue, setInputValue] = useState<string>("");
  const [todoList, setTodoList] = useState<string[]>([]);

  const handleInputTodolist = (event: React.ChangeEvent<HTMLInputElement>) => {
    let inputText = event.target.value;
    setInputValue(inputText);
  };
  const handleAdd = () => {
    let tempTodoList = todoList;
    tempTodoList.push(inputValue);
    setTodoList(tempTodoList);
    setInputValue("");
  };

  return (
    <>
      <div className="container mt-lg-5 ">
        <h1 className="text-center mb-3">Hello Todo list</h1>
        <div className="box">
          <div className="input-group w-50">
            <input type="text" className="form-control" onChange={handleInputTodolist} value={inputValue} />
            <button type="button" className="btn btn-primary input-group-text" onClick={handleAdd}>
              Add
            </button>
          </div>
        </div>

        <div className="row">
          <div className="col-lg-6">
            <h4>Todo List</h4>
            {todoList.map((item: string, index: number) => {
              return (
                <>
                  <div className="row mb-3">
                    <div className="col-8">

                    </div>
                    <div className="col-4 text-end">
                      <button type="button" className="btn btn-outline-warning me-3">
                        Edit
                      </button>
                      <button type="button" className="btn btn-danger">
                        Delete
                      </button>
                    </div>
                  </div>
                  <hr />
                </>
              );
            })}
          </div>
          <div className="col-lg-6"></div>
        </div>
      </div>
    </>
  );
};

export default TodoList;
