import React, { useState } from "react";

type TodoItem = {
  id: number;
  text: string;
};

const TodoList3: React.FC = () => {
  const [todos, setTodos] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");
  const [editTodoId, setEditTodoId] = useState<number | null>(null); // เพิ่มสถานะ editTodoId

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodos([
        ...todos,
        {
          id: Date.now(),
          text: newTodo,
        },
      ]);
      setNewTodo("");
    }
  };

  const handleDeleteTodo = (id: number) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

  const handleEditTodo = (id: number, newText: string) => {
    setTodos((prevTodos) => prevTodos.map((todo) => (todo.id === id ? { ...todo, text: newText } : todo)));
  };

  const handleEditTodoStart = (id: number) => {
    setEditTodoId(id); // ตั้งค่า editTodoId เป็น id ของรายการที่ถูกคลิก "Edit"
  };

  const handleSaveEdit = (id: number) => {
    handleEditTodo(id, newTodo); // เรียกใช้ handleEditTodo เพื่อบันทึกการแก้ไข
    setEditTodoId(null); // รีเซ็ต editTodoId เพื่อกลับไปใช้ข้อความธรรมดา
    setNewTodo(''); // รีเซ็ตค่า newTodo
  };


  return (
    <div>
      <h1>Todo List</h1>
      {/* ... */}
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            {editTodoId === todo.id ? ( // ใช้ตรวจสอบว่า editTodoId มีค่าเท่ากับ id ของรายการที่ถูกคลิกหรือไม่
              <input
                type="text"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
              />
            ) : (
              todo.text
            )}
            {editTodoId !== todo.id && (
              <>
                <button onClick={() => handleEditTodoStart(todo.id)}>Edit</button>
                <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
              </>
            )}

          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoList3;
