import React, { useState, useEffect } from "react";
import "./TodoList.css";

interface TodoItem {
  id: number;
  text: string;
}

const TodoList: React.FC = () => {
  const [idTodo, setIdTodo] = useState<number>(0);
  const [todoList, setTodoList] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");

  const [inputEditValue, setInputEditValue] = useState<string>("");
  const [isEdit, setIsEdit] = useState<boolean>(false);

  // const handleInputTodolist = (event: React.ChangeEvent<HTMLInputElement>) => {
  //   let inputText = event.target.value;
  //   setInputValue(inputText);
  // };
  // const handleAdd = () => {
  //   let tempTodoList = todoList;
  //   tempTodoList.push(inputValue);
  //   setTodoList(tempTodoList);
  //   setInputValue("");
  // };

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodoList([
        ...todoList,
        {
          id: idTodo,
          text: newTodo,
        },
      ]);
      setNewTodo("");
      let setID = idTodo + 1;
      setIdTodo(setID);
    }

  }

  const handleEdit = (id) => {
    console.log(id);
