    })
  }
  const handleEditInput = (event: React.ChangeEvent<HTMLInputElement>) => {
    let inputText = event.target.value;
    setInputEditValue(inputText);
  }
  console.log(todoList);
  console.log("ID:", idTodo);

  return (
    <>
      <div className="container mt-lg-5 ">
        <h1 className="text-center mb-3">Hello Todo list</h1>
        <div className="box">
          <div className="input-group w-50">
            <input type="text" className="form-control" onChange={(e) => setNewTodo(e.target.value)} value={newTodo} />
            <button type="button" className="btn btn-success input-group-text" onClick={handleAddTodo}>
              Add
            </button>
          </div>
        </div>

        <div className="row">
          <div className="col-6">
            <h4>Todo List</h4>
            <table className="table">
              <tbody>
                {todoList.map((item: TodoItem, index: number) => {
                  return (
                    <>
                      <tr key={index}>
                        <td className="col-10"><p className="mb-0">{item.id} {item.text}</p></td>
                        <td className="col-10">{isEdit ? <input type="text" className="form-control" onChange={handleEditInput} value={item}></input> : <p className="mb-0">{item}</p>}</td>
                        <td className="col-1"><button className="btn btn-outline-warning" onClick={() => handleEdit(index)}>Edit</button></td>
                        <td className="col-1"><button className="btn btn-danger">Delete</button></td>
                      </tr>
                    </>
                  );
                })}
              </tbody>
            </table>
          </div>
          <div className="col-6"></div>
        </div>
      </div>
    </>
  );
};

export default TodoList;
