import React, { useState, useEffect } from "react";
import "./TodoList.css";

const TodoList: React.FC = () => {
  const [inputValue, setInputValue] = useState<string>("");
  const [todoList, setTodoList] = useState<string[]>([]);
  const [isEdit, setIsEdit] = useState<boolean>(false);

  const handleInputTodolist = (event: React.ChangeEvent<HTMLInputElement>) => {
    let inputText = event.target.value;
    setInputValue(inputText);
  };
  const handleAdd = () => {
    let tempTodoList = todoList;
    tempTodoList.push(inputValue);
    setTodoList(tempTodoList);
    setInputValue("");
  };

  const handleEdit = (id) => {
    console.log(id);

  }

  return (
    <>
      <div className="container mt-lg-5 ">
        <h1 className="text-center mb-3">Hello Todo list</h1>
        <div className="box">
          <div className="input-group w-50">
            <input type="text" className="form-control" onChange={handleInputTodolist} value={inputValue} />
            <button type="button" className="btn btn-success input-group-text" onClick={handleAdd}>
              Add
            </button>
          </div>
        </div>

        <div className="row">
          <div className="col-6">
            <h4>Todo List</h4>
            <table className="table">
              <tbody>
                {todoList.map((item: string, index: number) => {
                  return (
                    <>
                      <tr key={index}>
                        {/* <td className="col-10"><p className="mb-0">{item}</p></td> */}
                        <td className="col-10">{isEdit ? <input type="text" className="form-control" value={item}></input> : <p className="mb-0">{item}</p>}</td>
                        <td className="col-1"><button className="btn btn-outline-warning" onClick={() => handleEdit(index)}>Edit</button></td>
                        <td className="col-1"><button className="btn btn-danger">Delete</button></td>
                      </tr>
                    </>
                  );
                })}
              </tbody>
            </table>
          </div>
          <div className="col-6"></div>
        </div>
      </div>
    </>
  );
};

export default TodoList;
