import React, {useState} from "react";
import "../TodoList/TodoList.css";

interface TodoItem {
  id: number;
  text: string;
  isEdit: boolean;
}

const TodoList2 = () => {
  const [todoList, setTodoList] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodoList(todoList, )
    }
  }

  return (
    <>
      <div className="content">
        <div className="box shadow-lg">
          <header>
            <h4>Hello Todo List 2</h4>
          </header>
          <section>
            <div className="add-todolist">
              <input type="text" className="form-control" value={newTodo} onChange={(e) => setNewTodo(e.target.value)} />
              <button className="btn btn-success" onClick={handleAddTodo}>Add</button>
            </div>
            <div className="todolist mt-3">
              <ul>
                {todoList.map((item: TodoItem, index: number) => {
                  return (<li key={index}>
                    <div className="list-item-text">
                      {item.isEdit ? <input defaultValue={item.text}/> : <p>{item.text}</p>}
                    </div>
                    <div className="list-item-btn"></div>
                  </li>)
                })}
              </ul>
            </div>
          </section>
        </div>
      </div>
    </>
  )
}
export default TodoList2
