import React, { useState, useEffect } from "react";
import "./TodoList.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import Box from "../Box";

interface TodoItem {
  id: number;
  text: string;
  isEdit: boolean;
}



const TodoList: React.FC = () => {
  const [idTodo, setIdTodo] = useState<number>(0);
  const [todoList, setTodoList] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");
  const [newTodoEdit, setNewTodoEdit] = useState<string>("");
  // const [newTodoEdit, setNewTodoEdit] = useState<string[]>([]);

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodoList([
        ...todoList,
        {
          id: idTodo,
          text: newTodo,
          isEdit: false,
        },
      ]);
      setNewTodo("");
      let setID = idTodo + 1;
      setIdTodo(setID);
    }
  };

  const handleEditTodo = (item: TodoItem) => {
    let sum = todoList.filter((todo: TodoItem) => {
      if (todo.id === item.id) {
        return (todo.isEdit = true);
      } else {
        return todo;
      }
    });
    setTodoList(sum);
  };

  const handleUpdate = (item: TodoItem) => {
    if (newTodoEdit.trim() !== "") {
      let updatedList = todoList.map((todo) =>
        todo.id === item.id ? { ...todo, text: newTodoEdit, isEdit: false } : todo
      );
      setTodoList(updatedList);
      setNewTodoEdit("");
    }
  };

  const handleDelete = (item: TodoItem) => {
    let deletedList = todoList.filter((todo) => {
      return todo.id !== item.id;
    });
    setTodoList(deletedList);
  };

  console.log(newTodoEdit);

  return (
    <>
      <div className="content">
        <div className="box shadow-lg">
          <header>
            <h4>Hello Todo List</h4>
          </header>
          <section>
            <div className="add-todolist">
              <input
                type="text"
                className="form-control"
                onChange={(e) => setNewTodo(e.target.value)}
                value={newTodo}
              />
              <button className="btn btn-success" onClick={handleAddTodo}>
                Add
              </button>
            </div>
            <div className="todolist mt-3">
              <ul>
                {todoList.map((item: TodoItem, index: number) => {
                  return (
                    <>
                      <li key={index} className="list-item mb-2">
                        <div className="w-75">
                          {item.isEdit ? (
                            <input
                              className="form-control"
                              defaultValue={item.text}
                              onChange={(e) => setNewTodoEdit(e.target.value)}
                            />
                          ) : (
                            <p>{item.text}</p>
                          )}
                        </div>
                        <div className="w-25 text-end">
                          {item.isEdit ? (
                            <button className="btn" onClick={() => handleUpdate(item)}>
                              <i className="bi bi-save-fill"></i>
                            </button>
                          ) : (
                            <button className="btn" onClick={() => handleEditTodo(item)}>
                              <i className="bi bi-pencil-square"></i>
                            </button>
                          )}
                          <button className="btn" onClick={() => handleDelete(item)}>
                            <i className="bi bi-trash3-fill"></i>
                          </button>
                        </div>
                      </li>
                    </>
                  );
                })}
              </ul>
            </div>
          </section>
        </div>
      </div>
    </>
  );
};

export default TodoList;
