import React, { useState, useEffect } from "react";
import "./TodoList.css";
import Box from './Box';

interface TodoItem {
  id: number;
  text: string;
  isEdit: boolean;
}

const TodoList: React.FC = () => {
  const [idTodo, setIdTodo] = useState<number>(0);
  const [todoList, setTodoList] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");
  const [newTodoEdit, setNewTodoEdit] = useState<string>("");

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodoList([
        ...todoList,
        {
          id: idTodo,
          text: newTodo,
          isEdit: false,
        },
      ]);
      setNewTodo("");
      let setID = idTodo + 1;
      setIdTodo(setID);
    }
  };

  const handleEditTodo = (item: TodoItem) => {
    let sum = todoList.filter((todo: TodoItem) => {
      if (todo.id === item.id) {
        return (todo.isEdit = true);
      } else {
        return todo;
      }
    });
    setTodoList(sum);
  };

  const handleUpdate = (item: TodoItem) => {
    if (newTodoEdit.trim() !== "") {
      let updatedList = todoList.map((todo) =>
        todo.id === item.id ? { ...todo, text: newTodoEdit, isEdit: false } : todo
      );
      setTodoList(updatedList);
    }
  };

  const handleDelete = (item: TodoItem) => {
    let deletedList = todoList.filter((todo) => {
      return todo.id !== item.id;
    });
    setTodoList(deletedList);
  };

  return (
    <>
      <div className="container mt-lg-5 content">
        <h1 className="text-center mb-3">Hello Todo list</h1>
        <div className="box">
          <div className="input-group w-50">
            <input type="text" className="form-control" onChange={(e) => setNewTodo(e.target.value)} value={newTodo} />
            <button type="button" className="btn btn-success input-group-text" onClick={handleAddTodo}>
              Add
            </button>
          </div>
        </div>

        <div className="row">
          <div className="col-8">
            <h4>Todo List</h4>
            <table className="table table-striped">
              <thead>
                <tr className="table-dark">
                  <td className="col-1">#</td>
                  <td className="col-9">List name</td>
                  <td className="col-1">Edit</td>
                  <td className="col-1">Delete</td>
                </tr>
              </thead>
              <tbody>
                {todoList.map((item: TodoItem, index: number) => {
                  return (
                    <tr key={index}>
                      <td className="col-1">{item.id}</td>
                      <td className="col-9">
                        {item.isEdit ? (
                          <>
                            <input
                              className="form-control"
                              onChange={(e) => setNewTodoEdit(e.target.value)}
                              defaultValue={item.text}
                            />
                          </>
                        ) : (
                          <p>{item.text}</p>
                        )}
                      </td>
                      <td className="col-1">
                        {item.isEdit ? (
                          <button className="btn btn-warning" onClick={() => handleUpdate(item)}>
                            Update
                          </button>
                        ) : (
                          <button className="btn btn-outline-warning" onClick={() => handleEditTodo(item)}>
                            Edit
                          </button>
                        )}
                      </td>
                      <td className="col-1">
                        <button className="btn btn-danger" onClick={() => handleDelete(item)}>
                          Delete
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          <div className="col-4"></div>
        </div>
      </div>
    </>
  );
};

export default TodoList;
