import React, { useState, useEffect } from "react";
import "./TodoList.css";

interface TodoItem {
  id: number;
  text: string;
  isEdit: boolean;
}

const TodoList: React.FC = () => {
  const [idTodo, setIdTodo] = useState<number>(0);
  const [todoList, setTodoList] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");

  // const [isEdit, setIsEdit] = useState<boolean>(false);

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodoList([
        ...todoList,
        {
          id: idTodo,
          text: newTodo,
          isEdit: false,
        },
      ]);
      setNewTodo("");
      let setID = idTodo + 1;
      setIdTodo(setID);
    }
  };

  const handleEditTodo = (id: number, newText: string) => {

    setIsEdit(true);
    setTodoList((prevTodos) => prevTodos.map((todo) => (todo.id === id ? { ...todo, text: newText } : todo)));

  };


  return (
    <>
      <div className="container mt-lg-5 ">
        <h1 className="text-center mb-3">Hello Todo list</h1>
        <div className="box">
          <div className="input-group w-50">
            <input type="text" className="form-control" onChange={(e) => setNewTodo(e.target.value)} value={newTodo} />
            <button type="button" className="btn btn-success input-group-text" onClick={handleAddTodo}>
              Add
            </button>
          </div>
        </div>

        <div className="row">
          <div className="col-6">
            <h4>Todo List</h4>
            <table className="table">
              <tbody>
                {todoList.map((item: TodoItem, index: number) => {
                  return (
                    <>
                      <tr key={index}>
                        {/* <td className="col-10">
                          {isEdit ? (
                            <input type="text" className="form-control" value={item.text} id={`inputEdit${index}`}></input>
                          ) : (
                            <p className="mb-0">
                              {item.id} {item.text}
                            </p>
                          )}
                        </td> */}
                        <div className="col-10">
                          {item.isEdit ? <></> : <></>}
                        </div>
                        <td className="col-1">
                          <button className="btn btn-outline-warning" onClick={() => handleEditTodo(item.id, item.text)}>Edit</button>
                        </td>
                        <td className="col-1">
                          <button className="btn btn-danger">Delete</button>
                        </td>
                      </tr>
                    </>
                  );
                })}
              </tbody>
            </table>
          </div>
          <div className="col-6"></div>
        </div>
      </div>
    </>
  );
};

export default TodoList;
