import React, { useState } from "react";
import "../TodoList/TodoList.css";

interface TodoItem {
  id: number;
  text: string;
  isEdit: boolean;
}

const TodoList2 = () => {
  const [todoList, setTodoList] = useState<TodoItem[]>([]);
  const [newTodo, setNewTodo] = useState<string>("");
  const [todoID, setTodoID] = useState<number>(0);

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      setTodoList([...todoList, { id: todoID, text: newTodo, isEdit: false }]);
      setNewTodo("");
      setTodoID(todoID + 1);
    }
  };

